import React from 'react'

const Display = ({countries, newFilter}) =>{
    const countriesToShow = countries.filter((country)=>country.name.toLowerCase().includes(newFilter))
    if (countriesToShow.length === 1)
        return (
            <div>
                <h2>{countriesToShow[0].name}</h2>
            </div>
        )
    else if (countriesToShow.length>10)
        return (
            <div>
                Too many matches, specify another filter
            </div>
        )
    else // 1<length<=10
        return(
            <div>
                {countriesToShow.map((country, i)=><p key={i}> {country.name}</p>)}
            </div>
        )
}

export default Display
