import React, { useEffect } from 'react'
import axios from 'axios'

const Display = ({countries, newFilter, setNewFilter}) =>{
    const countriesToShow = countries.filter((country)=>country.name.toLowerCase().includes(newFilter.toLowerCase()))
    const api_key = process.env.REACT_APP_WEATHER_API_KEY

    const handleClick = (countryName) =>{
        setNewFilter(countryName)
    }
    const get_weather = (country) => axios.get(`http://api.weatherstack.com/current?access_key=${api_key}&query=${country.name}`)
            .then(response=>{
                console.log(response.data.current.temperature)
                return response.data.current
            })
    if (countriesToShow.length === 1){
        const country = countriesToShow[0]
        const weather_info = get_weather(country)
        console.log(weather_info.temperature)
        return (
            <div>
                <h2>{country.name}</h2>
                <p>
                    captial {country.capital}
                </p>
                <p>
                    population {country.population}
                </p>
                <h3>languages</h3>
                <ul>
                    {country.languages.map((lang, i)=><li key={i}>{lang.name}</li>)}
                </ul>
                <img src={country.flag} alt="image not found" width="250" height="250"/>
                <h3>Weather in {country.name}</h3>
                <p>temperature:</p>
            </div>
        )
    }
    else if (countriesToShow.length>10)
        return (
            <div>
                Too many matches, specify another filter
            </div>
        )
    else // 1<length<=10
        return(
            <div>
                {countriesToShow.map((country, i)=>
                    <p key={i}> 
                        {country.name} 
                        <button onClick={()=> handleClick(country.name)}>
                            show more
                        </button>
                    </p>
                    )
                }
               
            </div>
        )
}

export default Display
