import React from 'react'

const Display = ({countries, newFilter}) =>{
    const countriesToShow = countries.map((country)=>country.name).filter((name)=>name.toLowerCase().includes(newFilter))
    
    console.log(countries[0].name)
    
    return(
        <div>
            {countriesToShow.map((country, i)=><p key={i}> {country}</p>)}
        </div>
    )
}

export default Display
