import React from 'react'

const Display = ({countries, newFilter}) =>{
    const countriesToShow = countries.filter((country)=>country.name.toLowerCase().includes(newFilter))
    if (countriesToShow.length === 1){
        const country = countriesToShow[0]
        return (
            <div>
                <h2>{country.name}</h2>
                <p>
                    captial {country.capital}
                </p>
                <p>
                    population {country.population}
                </p>
                <h3>languages</h3>
                <ul>
                    {country.languages.map((lang, i)=><li key={i}>{lang.name}</li>)}
                </ul>
                <img src={country.flag} width="150" height="300"/>
            </div>
        )
    }
        
    else if (countriesToShow.length>10)
        return (
            <div>
                Too many matches, specify another filter
            </div>
        )
    else // 1<length<=10
        return(
            <div>
                {countriesToShow.map((country, i)=><p key={i}> {country.name}</p>)}
            </div>
        )
}

export default Display
