import React from 'react'

const Persons = ({personsToShow, handleDelete})=>{
    return (
        <div>
            {personsToShow.map((person, i) =>  
            <p key={i}> 
            {person.name} {person.number}  <button onClick={()=>handleDelete(i+1)}> delete </button>
            </p>
            )
            }
        </div>
    )
    
}

export default Persons
