import React, { useState, useEffect } from 'react'
import Filter from './components/Filter'
import PersonForm from './components/PersonForm'
import Persons from './components/Persons'
import personsService from './services/persons'

const App = () => {
  const [ persons, setPersons ] = useState([])
  const [ newName, setNewName ] = useState('')
  const [ newNumber, setNewNumber ] = useState('')
  const [ newFilter, setNewFilter ] = useState('')



  useEffect(() => {
    personsService
    .getAll()
    .then(response => setPersons(response))
  }, [])

  const handleNameChange = (event) => setNewName(event.target.value)  
  const handleNumberChange = (event) => setNewNumber(event.target.value)
  const handleFilterChange = (event) => setNewFilter(event.target.value)
  const personsToShow = newFilter ? persons.filter(person=>person.name.toLowerCase().includes(newFilter)) : persons

  const handleUpdate = (person) =>{
    personsService
    .update(person)
    .then(
      response=>setPersons(response)
    )
  }

  const handleSubmit = (event)=>{
    event.preventDefault()
    const person_to_add = persons.find((person)=>person.name===newName)
    if (person_to_add && person_to_add.number!==newNumber)
      if (window.confirm(`${person_to_add.name} is already added to phonebook, 
      replace the old number with a new one?`)) { 
        handleUpdate(person)
      }
    else if (persons.map((person)=>person.number).includes(newNumber))
      alert(`${newNumber} already exists`)
    else{
      personsService
      .addNew({name:newName, number:newNumber})
      .then(response=>
        setPersons(persons.concat(response))
      )}
    setNewName('')
    setNewNumber('')
  }

  const handleDelete = ({id ,name , number}) =>{
    if (window.confirm(`Delete ${name}?`)) { 
      personsService
      .deleteEntry(id)
      setPersons(persons.filter(person=>(person.name!==name && person.number!==number)))
      console.log('deleted', name)
    }

  }


  return (
    <div>
      <h2>Phonebook</h2>{
        <Filter newFilter={newFilter} handleFilterChange={handleFilterChange}/>
      }
      <h2>add a new</h2>
      <PersonForm newName={newName} handleNameChange={handleNameChange} 
      newNumber={newNumber} handleNumberChange={handleNumberChange} 
      handleSubmit={handleSubmit}
      />
      <h2>Numbers</h2>
      <Persons personsToShow={personsToShow} handleDelete={handleDelete}/>
    </div>
  )
}

export default App
