import React, { useState } from 'react'
import Filter from './components/Filter'

const App = () => {
  const [persons, setPersons] = useState([
    { name: 'Arto Hellas', number: '040-123456' },
    { name: 'Ada Lovelace', number: '39-44-5323523' },
    { name: 'Dan Abramov', number: '12-43-234345' },
    { name: 'Mary Poppendieck', number: '39-23-6423122' }
  ])
  const [ newName, setNewName ] = useState('')
  const [ newNumber, setNewNumber ] = useState('')
  const [ newFilter, setNewFilter ] = useState('')

  const handleNoteChange = (event)=> setNewName(event.target.value)  
  const handleNumberChange = (event) => setNewNumber(event.target.value)
  const handleFilterChange = (event) => setNewFilter(event.target.value)
  const personsToShow = newFilter ? persons.filter(person=>person.name.toLowerCase().includes(newFilter)) : persons


  const handleSubmit = (event)=>{
    event.preventDefault()
    if (persons.map((person)=>person.name).includes(newName))
      alert(`${newName} is already added to phonebook`)
    else if (persons.map((person)=>person.number).includes(newNumber))
      alert(`${newNumber} already exists`)
    else
      setPersons(persons.concat({name: newName, number:newNumber}))

    setNewName('')
    setNewNumber('')
  }



  return (
    <div>
      <h2>Phonebook</h2>{
        <Filter newFilter={newFilter} handleFilterChange={handleFilterChange}/>
      }
      <h2>add a new</h2>
      <form onSubmit={handleSubmit}>
        <div>
          name: <input value={newName} onChange={handleNoteChange}/>
        </div>
        <div>
          number: <input value={newNumber} onChange={handleNumberChange}/>
        </div>
        <div>
          <button type="submit">add</button>
        </div>
      </form>
      <h2>Numbers</h2>
      {personsToShow.map((person, i) => <p key={i}>{person.name} {person.number}</p>)}
    </div>
  )
}

export default App
