import axios from 'axios';
const base_url = 'http://localhost:3001/persons';

const getAll = () => axios.get(base_url).then(response => response.data);

const addNew = ({
  newNumber,
  newName
}) => axios.post(base_url, {
  name: newName,
  number: newNumber
}).then(response => response.data);

export default {
  getAll,
  addNew
};
